import { Component } from "@angular/core";

@Component({
    selector: "product-list",
    templateUrl: "./product-list.component.html",
    styleUrls: ["./product-list.component.css"],
})
export class ProductListComponent {
    name = "John Smith";
    addToCart: number = 0;
    product = {
        name: "iPhone X",
        price: 789,
        color: "Black",
        discount: 8.5,
        inStock: 10,
        pImage: "/assets/images/iphone.png",
    };

    getDiscountedPrice() {
        return (
            this.product.price -
            (this.product.price * this.product.discount) / 100
        );
    }

    onNameChange(event: any) {
        this.name = event.target.value;
        console.log(event.target.id);
    }

    decrementCartValue() {
        if (this.addToCart > 0) {
            this.addToCart--;
        }
    }

    incrementCartValue() {
        if (this.addToCart < this.product.inStock) {
            this.addToCart++;
        }
    }
}
<div class="ekart--product--container">
    <img [src]="product.pImage" />
    <p>Name: {{ product.name }}</p>
    <p>Price: {{ "$" + product.price }}</p>
    <p>Color: {{ product.color }}</p>
    <p>Discounted Price: {{ getDiscountedPrice().toFixed(2) }}</p>
    <p>
        {{
            product.inStock > 0
                ? "Only" + product.inStock + " items left"
                : "Not in Stock"
        }}
    </p>
    <button (click)="decrementCartValue()" id="decrement">-</button>
    <span>{{ addToCart }}</span>
    <button (click)="incrementCartValue()" id="increment">+</button>
    <input (input)="onNameChange($event)" id="input-main" /><br />
    <p>name: {{ name }}</p>
    <app-search></app-search>
</div>

